import React, { useState, useEffect } from "react";
import axios from "axios";

function Register({ setPage }) {
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    email: "",
    contactNo: "",
    permanentAddress: "",
    temporaryAddress: "",
    gender: "",
    qualification: "",
    company: "",
    role: "",
    username: "",
    password: "",
    confirmPassword: "",
  });

  const [errors, setErrors] = useState({});

  // Run initial validation so mandatory fields show errors immediately
  useEffect(() => {
    setErrors(validateForm(formData));
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
    // clear error while typing
    setErrors((prev) => ({ ...prev, [name]: "" }));
  };

  const validateForm = (data) => {
    let newErrors = {};

    if (!data.firstName.trim()) newErrors.firstName = "First name is required";
    if (!data.lastName.trim()) newErrors.lastName = "Last name is required";

    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!data.email || !emailRegex.test(data.email)) {
      newErrors.email = "Valid email is required";
    }

    const phoneRegex = /^[0-9]{10,}$/;
    if (!data.contactNo || !phoneRegex.test(data.contactNo)) {
      newErrors.contactNo = "Valid contact number is required";
    }

    if (!data.gender) newErrors.gender = "Gender is required";

    if (!data.username.trim()) newErrors.username = "Username is required";

    if (data.password.length < 6) {
      newErrors.password = "Password must be at least 6 characters";
    }

    if (data.password !== data.confirmPassword) {
      newErrors.confirmPassword = "Passwords do not match";
    }

    return newErrors;
  };

  // validate single field on blur
  const handleBlur = (e) => {
    const { name } = e.target;
    const fieldErrors = validateForm(formData);
    if (fieldErrors[name]) {
      setErrors((prev) => ({ ...prev, [name]: fieldErrors[name] }));
    }
  };

  const handleRegister = async () => {
    const newErrors = validateForm(formData);
    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
      return;
    }

    try {
      const res = await axios.post("http://127.0.0.1:8000/register", formData);
      alert(res.data.message);
      setPage("login");
    } catch (err) {
      alert(err.response?.data?.detail || "Registration failed");
    }
  };

  const renderInput = (name, placeholder, type = "text", required = true) => (
    <div className="mb-2">
      <input
        className={`form-control form-control-sm ${errors[name] ? "is-invalid" : ""}`}
        name={name}
        type={type}
        placeholder={required ? `${placeholder} *` : placeholder}
        value={formData[name]}
        onChange={handleChange}
        onBlur={handleBlur}
      />
      {errors[name] && <div className="invalid-feedback">{errors[name]}</div>}
    </div>
  );

  return (
    <div className="container mt-4">
      <div className="card shadow-sm border-0">
        <div className="card-body p-4">
          <h3 className="text-center text-primary mb-3">Create Your Account</h3>
          <p className="text-muted text-center mb-4">
            Fields marked with * are mandatory.
          </p>

          <div className="row g-2">
            <div className="col-md-6">{renderInput("firstName", "First Name")}</div>
            <div className="col-md-6">{renderInput("lastName", "Last Name")}</div>
          </div>

          {renderInput("email", "Email", "email")}
          {renderInput("contactNo", "Contact No")}
          {renderInput("permanentAddress", "Permanent Address", "text", false)}
          {renderInput("temporaryAddress", "Temporary Address", "text", false)}

          <div className="mb-2">
            <select
              className={`form-select form-select-sm ${errors.gender ? "is-invalid" : ""}`}
              name="gender"
              value={formData.gender}
              onChange={handleChange}
              onBlur={handleBlur}
            >
              <option value="">Select Gender *</option>
              <option value="Male">Male</option>
              <option value="Female">Female</option>
              <option value="Other">Other</option>
            </select>
            {errors.gender && <div className="invalid-feedback">{errors.gender}</div>}
          </div>

          {renderInput("qualification", "Highest Qualification", "text", false)}
          {renderInput("company", "Company", "text", false)}
          {renderInput("role", "Role", "text", false)}
          {renderInput("username", "Username")}
          {renderInput("password", "Set Password", "password")}
          {renderInput("confirmPassword", "Confirm Password", "password")}

          <button className="btn btn-success w-100 mt-3" onClick={handleRegister}>
            Register
          </button>
          <button className="btn btn-link w-100 mt-2" onClick={() => setPage("login")}>
            Back to Login
          </button>
        </div>
      </div>
    </div>
  );
}

export default Register;
